import React, { Component } from 'react'
import Header from './components/Header'
import NavList from './components/NavList'
import './App.css'

export default class App extends Component {

    props = {
        items: []
    }

    insertNavItem(id, to, itemName) {
		const item = {
			id: {id},
			to: {to},
			itemName: {itemName}
		}

        this.props.items.push(item)
    }

	show = ()=>{
		const i1 = this.input1.current.value
		const i2 = this.input2.current.value
		const i3 = this.input3.current.value
		
		alert('添加的内容是：'+i1+i2+i3)
		this.insertNavItem(i1, i2, i3)
	}

	render() {
		return (
			<div>
				<div className="header">
					<Header/>
					<input ref={this.input1}></input>
					<input ref={this.input2}></input>
					<input ref={this.input3}></input>
					<button onClick={this.show}>添加</button>
				</div>
				<div className="nav-list">
					<NavList/>
				</div>
			</div>
		)
	}
}
