import React, { Component } from 'react'
import Header from './components/Header'
import NavList from './components/NavList'
import './App.css'

export default class App extends Component {

    props = {
        items: []
	}
	
	input1 = React.createRef();
	input2 = React.createRef();
	input3 = React.createRef();

    insertNavItem(id, to, itemName) {
		console.log(this)
		let p = this.props.items
		console.log(p)
		
        p = p.push({
			id:{id},
			to:{to},
			itemName:{itemName}
		})
		console.log(p)

		this.props.items = p
    }

	show = ()=>{
		console.log(this.input1)
		const i1 = this.input1.current.value
		const i2 = this.input2.current.value
		const i3 = this.input3.current.value
		
		alert('添加的内容是：'+i1+i2+i3)
		this.insertNavItem(i1, i2, i3)
	}

	render() {
		return (
			<div>
				<div className="header">
					<Header/>
					<input ref={this.input1} type="text" ></input>
					<input ref={this.input2} type="text" ></input>
					<input ref={this.input3} type="text" ></input>
					<button onClick={this.show}>添加</button>
				</div>
				<div className="nav-list">
					<NavList/>
				</div>
			</div>
		)
	}
}
